import React, { useEffect, useState, useContext } from 'react';
import axios from 'axios';
import { AuthContext } from '../../Hooks/AuthContext';
import "./Notification.css";

const NotificationsList = ({ }) => {
  const [notifications, setNotifications] = useState([]);
  const authContext = useContext(AuthContext);

  // Define a useEffect to fetch notifications when the component mounts and when the username changes.
  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const response = await axios.get('http://localhost:1024/get_notification', {
          headers: {
            'X-Username': username, // Send the username in a custom header
          },
        });
        setNotifications(response.data);
      } catch (error) {
        console.error('Error fetching notifications:', error);
      }
    };

    // Call the fetchNotifications function when the component mounts or when username changes.
    fetchNotifications();
  }, []); // Specify [username] as the dependency array.

  return (
    <div>
      <h1>Notifications</h1>
      <ul>
        {notifications.map((notification, index) => (
          <li key={index}>{notification}</li>
        ))}
      </ul>
    </div>
  );
};

export default NotificationsList;
